import React, { useEffect, useState } from "react"
import { useCalendar } from "../../hooks/useCalendar"
import dayjs from "dayjs"

  
export const MainCalandarView = () => {
    const now = dayjs()
    //day
    const {
        showDayJs,
        getCalendarColumns,
        dayOfWeek,
        getDay
    } = useCalendar()
    const [calendar,setCalendar] = useState(getCalendarColumns(now))
    useEffect(()=>{
        console.log(calendar)
    },[])

    const CalendarDayBox = ({value,index}:any) =>{
        console.log(value)
        return(
                <div style={{
                    border:'1px solid black',
                    height:'14.5vh',
                }}>{getDay(value)}</div>
        )
    }
    
    return(
        <div style={{border:'1px solid black',width:"75%",height:'80vh'}}>
            <div style={{
                display:"flex",
                flexDirection:"row",
                
                }}>
                {

                    dayOfWeek.map((value,index)=>{
                        return(
                            <div style={{
                                border:'1px solid black',
                                flex:1/7,
                                height:'6vh'
                            }}>{value}</div>
                        )
                    })
                }
            </div>
            <div style={{display: 'grid', gridTemplateColumns: 'repeat(7, 1fr)'}}>
            {calendar.map((value:any,index:any)=>{
                return(
                    <CalendarDayBox value={value} index={index}/>
                )
            })}
            </div>
            
        </div>
    )
}
