import React, { useEffect, useState } from "react"
import { useCalendar } from "../../hooks/useCalendar"
import dayjs from "dayjs"

  
export const MainCalandarView = () => {
    const now = dayjs()
    //day
    const {
        showDayJs,
        getCalendarColumns,
        dayOfWeek,
        getDay
    } = useCalendar()
    const [calendar,setCalendar] = useState(getCalendarColumns(now))
    useEffect(()=>{
        // console.log(calendar)
    },[])

    const CalendarDayBox = ({value,index}:any) =>{
        // console.log(value)
        if(value == null) {
            return(
                <div style={{
                    width:'100px',
                    border:'1px solid black',
                    height:'100px',
                }}/>
            )
        }
        else{
        return(
                <div style={{
                    width:'100px',
                    border:'1px solid black',
                    height:'100px',
                }}>{getDay(value)}</div>
        )
    }
    }
    
    return(
        <div style={{
            border:'1px solid black',}}>
            <h2>??</h2>
            <div style={{
                display:"flex",
                flexDirection:"row",
                }}>
                {
                    dayOfWeek.map((value,index)=>{
                        return(
                            <div 
                            key={index}
                            style={{
                                border:'1px solid black',
                                width:100,
                                height:'6vh'
                            }}>{value}</div>
                        )
                    })
                }
            </div>
            <div style={{
                display: 'grid',
                
                gridTemplateColumns: 'repeat(7, 1fr)',
                border: '1px solid blue',
                backgroundColor:'red'
                }}>
            {calendar.map((value:any,index:any)=>{
                return(
                    <CalendarDayBox value={value} index={index} key={index}/>
                )
            })}
            </div>
            
        </div>
    )
}
