import dayjs from 'dayjs'

export const useCalendar = () =>{
   
   const showDayJs = () =>{
    const now = dayjs()
    console.log(now)
    console.log(
        "1. set minute - hh",
        dayjs(now).format("YYYY.MM.DD hh:mm:ss a A")
    )
   }
    return{
        showDayJs
    }
}
